package tekarch.java.day1;

public class circlePerimetre_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//print area of  and perimeter 2pir circle
		float pi=3.142f;
		float radius=2;
		double area=pi*(radius*radius);
		double perime=2*(pi*radius);
	System.out.println("area of circle is" + area);
	System.out.println("perime of circle is" + perime);
	}
}